import React, { useState, useEffect } from "react";
import styled, { keyframes } from "styled-components";


const StyledTypingAnimation = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100vh;
`;

const typingEffect = keyframes`
  from {
    width: 0;
  }
  to {
    width: 100%;
  }
`;

const glitchEffect = keyframes`
  0% {
    transform: translate(0);
  }
  20% {
    transform: translate(-5px, 5px);
  }
  40% {
    transform: translate(5px, -5px);
  }
  60% {
    transform: translate(-5px, -5px);
  }
   80% {
    transform: translate(5px, 5px);
  }
  100% {
    transform: translate(0);
  }
`;

const StyledText = styled.p`
  font-size: 3rem;
  font-weight: bold;
  text-align: center;
  overflow: hidden;
  white-space: nowrap;
  color: ${(props) => props.color};
  &:after {
    content: "|";
    display: inline-block;
    margin-left: 5px;
    animation: ${typingEffect} 0.7s infinite;
  }
  &:hover {
    color: white;
    text-shadow: 0 0 5px white, 0 0 10px white, 0 0 20px white, 0 0 40px ${getCurrentColor};
    animation: ${glitchEffect} 1s linear infinite;
  }
`;

const colors = ["#FF4136", "#2ECC40", "#0074D9"];

const getCurrentColor = (index) => {
  return colors[index % colors.length];
};



export const TypingAnimation = ({ textArray, typingSpeed }) => {
  const [text, setText] = useState("");
  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    const timer = setTimeout(() => {
      const currentText = textArray[currentIndex];
      setText(currentText.slice(0, text.length + 1));
      if (text.length === currentText.length) {
        setTimeout(() => {
          setCurrentIndex((currentIndex + 1) % textArray.length);
        }, 2000);
      }
    }, typingSpeed);
    return () => clearTimeout(timer);
  }, [text, currentIndex, textArray, typingSpeed]);

  return (
    <StyledTypingAnimation>
      <StyledText color={getCurrentColor(currentIndex)}>{text}</StyledText>
    </StyledTypingAnimation>
  );
};
